mapper.select:
select *
from l_user
where user_name = #{ user_name }

select *
from l_user
where user_email = #{ user_email }

update l_user
set user_name = #{ user_name }		
where user_no = #{ user_no }

update l_user
set user_badge = #{ user_badge }		
where user_no = #{ user_no }

mapper.update:
update l_user
set	login_ok = #{ login_ok }
where user_no = #{ user_no }

update l_user
set	user_admin = #{ user_admin }
where user_no = #{ user_no }
